// src/components/time/TimePickerIOS.jsx

import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

export default function TimePickerIOS({ value, onChange }) {
  const [hours, setHours] = useState([]);
  const [minutes, setMinutes] = useState([]);

  const [selectedHour, setSelectedHour] = useState("00");
  const [selectedMinute, setSelectedMinute] = useState("00");

  useEffect(() => {
    // cria lista 00-23
    const h = [];
    for (let i = 0; i < 24; i++) h.push(String(i).padStart(2, "0"));
    setHours(h);

    // cria lista 00-59
    const m = [];
    for (let i = 0; i < 60; i++) m.push(String(i).padStart(2, "0"));
    setMinutes(m);
  }, []);

  // quando o "value" vier do CreateTask, aplica nele
  useEffect(() => {
    if (value) {
      const [h, m] = value.split(":");
      setSelectedHour(h);
      setSelectedMinute(m);
    }
  }, [value]);

  const handleHourScroll = (e) => {
    const index = Math.round(e.target.scrollTop / 40);
    const hour = hours[index] || "00";
    setSelectedHour(hour);
    onChange(`${hour}:${selectedMinute}`);
  };

  const handleMinuteScroll = (e) => {
    const index = Math.round(e.target.scrollTop / 40);
    const minute = minutes[index] || "00";
    setSelectedMinute(minute);
    onChange(`${selectedHour}:${minute}`);
  };

  // posição inicial na lista
  const hourIndex = hours.indexOf(selectedHour);
  const minuteIndex = minutes.indexOf(selectedMinute);

  return (
    <div className="flex justify-center items-center gap-8 py-4">
      
      {/* HORAS */}
      <div className="relative w-20 h-40 overflow-hidden">
        {/* highlight azul */}
        <div className="absolute top-1/2 -translate-y-1/2 left-0 right-0 border-t border-b border-blue-500 pointer-events-none" />

        <div
          className="overflow-y-scroll h-full scrollbar-none text-center"
          onScroll={handleHourScroll}
          ref={(ref) => {
            if (ref) ref.scrollTop = hourIndex * 40;
          }}
        >
          {hours.map((h) => (
            <motion.div
              key={h}
              className={`h-10 flex items-center justify-center text-lg ${
                h === selectedHour ? "text-blue-600 font-bold" : "text-gray-400"
              }`}
            >
              {h}
            </motion.div>
          ))}
        </div>
      </div>

      {/* MINUTOS */}
      <div className="relative w-20 h-40 overflow-hidden">
        {/* highlight azul */}
        <div className="absolute top-1/2 -translate-y-1/2 left-0 right-0 border-t border-b border-blue-500 pointer-events-none" />

        <div
          className="overflow-y-scroll h-full scrollbar-none text-center"
          onScroll={handleMinuteScroll}
          ref={(ref) => {
            if (ref) ref.scrollTop = minuteIndex * 40;
          }}
        >
          {minutes.map((m) => (
            <motion.div
              key={m}
              className={`h-10 flex items-center justify-center text-lg ${
                m === selectedMinute ? "text-blue-600 font-bold" : "text-gray-400"
              }`}
            >
              {m}
            </motion.div>
          ))}
        </div>
      </div>

    </div>
  );
}
