import React, { useEffect, useRef, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Button } from "../ui/button";

export default function IOSPicker({ initialTime = "", onClose, onConfirm }) {
  const hours = Array.from({ length: 24 }, (_, i) => String(i).padStart(2, "0"));
  const minutes = Array.from({ length: 60 }, (_, i) => String(i).padStart(2, "0"));

  // Se inicialTime vier vazio → usa hora atual
  const now = new Date();
  const [selectedHour, setSelectedHour] = useState(
    initialTime ? initialTime.split(":")[0] : String(now.getHours()).padStart(2, "0")
  );
  const [selectedMinute, setSelectedMinute] = useState(
    initialTime ? initialTime.split(":")[1] : String(now.getMinutes()).padStart(2, "0")
  );

  const hourRef = useRef(null);
  const minuteRef = useRef(null);

  const ITEM_HEIGHT = 48; // altura de cada item

  const handleSnap = (ref, list, setter) => {
    if (!ref.current) return;

    const scrollTop = ref.current.scrollTop;
    const index = Math.round(scrollTop / ITEM_HEIGHT);

    const finalIndex = Math.max(0, Math.min(index, list.length - 1));

    ref.current.scrollTo({
      top: finalIndex * ITEM_HEIGHT,
      behavior: "smooth",
    });

    setter(list[finalIndex]);
  };

  // Move o scroll para a posição correta no início
  useEffect(() => {
    const hourIndex = hours.indexOf(selectedHour);
    const minIndex = minutes.indexOf(selectedMinute);

    if (hourRef.current) hourRef.current.scrollTop = hourIndex * ITEM_HEIGHT;
    if (minuteRef.current) minuteRef.current.scrollTop = minIndex * ITEM_HEIGHT;
  }, []);

  return (
    <AnimatePresence>
      <motion.div
        className="fixed inset-0 bg-black/50 z-50 flex items-end justify-center"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        onClick={onClose}
      >
        <motion.div
          className="bg-white rounded-t-3xl w-full max-w-md p-6 pb-10 shadow-2xl"
          initial={{ y: 200 }}
          animate={{ y: 0 }}
          exit={{ y: 200 }}
          onClick={(e) => e.stopPropagation()}
        >
          <h2 className="text-xl font-bold text-center mb-4">Selecionar Horário</h2>

          <div className="relative flex justify-center gap-6 py-4">

            {/* LINHAS CENTRAIS ALINHADAS */}
            <div className="absolute left-0 right-0 top-[50%] h-[48px] -translate-y-1/2 border-y-2 border-blue-400 pointer-events-none" />

            {/* HORAS */}
            <div
              ref={hourRef}
              onScroll={() => handleSnap(hourRef, hours, setSelectedHour)}
              className="w-20 h-48 overflow-y-scroll snap-y snap-mandatory text-center scrollbar-none"
            >
              {hours.map((h) => (
                <div
                  key={h}
                  className={`snap-start h-12 flex items-center justify-center text-xl font-bold transition-all ${
                    selectedHour === h ? "text-blue-600 scale-110" : "text-gray-400"
                  }`}
                >
                  {h}
                </div>
              ))}
            </div>

            {/* MINUTOS */}
            <div
              ref={minuteRef}
              onScroll={() => handleSnap(minuteRef, minutes, setSelectedMinute)}
              className="w-20 h-48 overflow-y-scroll snap-y snap-mandatory text-center scrollbar-none"
            >
              {minutes.map((m) => (
                <div
                  key={m}
                  className={`snap-start h-12 flex items-center justify-center text-xl font-bold transition-all ${
                    selectedMinute === m ? "text-blue-600 scale-110" : "text-gray-400"
                  }`}
                >
                  {m}
                </div>
              ))}
            </div>
          </div>

          <Button
            className="w-full h-14 text-lg font-bold bg-blue-600 hover:bg-blue-700 text-white mt-3"
            onClick={() => onConfirm(`${selectedHour}:${selectedMinute}`)}
          >
            Confirmar
          </Button>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
}
