export function Calendar({ className = "", selected, onSelect, ...props }) {
  const value = selected instanceof Date
    ? selected.toISOString().split("T")[0]
    : "";

  return (
    <input
      type="date"
      className={`border rounded-md px-3 py-2 ${className}`}
      value={value}
      onChange={(e) => {
        const v = e.target.value;

        const date = v ? new Date(v + "T00:00:00") : null;

        if (onSelect) {
          onSelect(date);
        }
      }}
      {...props}
    />
  );
}
