import React, { useEffect, useRef, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Button } from "../ui/button";

export default function IOSPicker({ initialTime = "", onClose, onConfirm }) {
  const hours = Array.from({ length: 24 }, (_, i) => String(i).padStart(2, "0"));
  const minutes = Array.from({ length: 60 }, (_, i) => String(i).padStart(2, "0"));

  // Se vier vazio, usa hora atual
  const now = new Date();
  const [selectedHour, setSelectedHour] = useState(
    initialTime ? initialTime.split(":")[0] : String(now.getHours()).padStart(2, "0")
  );
  const [selectedMinute, setSelectedMinute] = useState(
    initialTime ? initialTime.split(":")[1] : String(now.getMinutes()).padStart(2, "0")
  );

  const hourRef = useRef(null);
  const minuteRef = useRef(null);

  // Faz o scroll travar no item correto
  const handleSnap = (ref, values, setState) => {
    if (!ref.current) return;

    const container = ref.current;
    const index = Math.round(container.scrollTop / 48); // 48px = altura do item
    const value = values[index];
    setState(value);
  };

  // Move o scroll para a posição inicial
  useEffect(() => {
    const hourIndex = hours.indexOf(selectedHour);
    const minIndex = minutes.indexOf(selectedMinute);

    if (hourRef.current) hourRef.current.scrollTop = hourIndex * 48;
    if (minuteRef.current) minuteRef.current.scrollTop = minIndex * 48;
  }, []);

  return (
    <AnimatePresence>
      <motion.div
        className="fixed inset-0 bg-black/50 z-50 flex items-end justify-center"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        onClick={onClose}
      >
        <motion.div
          className="bg-white rounded-t-3xl w-full max-w-md p-6 pb-10 shadow-2xl"
          initial={{ y: 200 }}
          animate={{ y: 0 }}
          exit={{ y: 200 }}
          transition={{ type: "spring", stiffness: 200 }}
          onClick={(e) => e.stopPropagation()}
        >
          <h2 className="text-xl font-bold text-center mb-4">Selecionar Horário</h2>

          {/* ÁREA DO PICKER */}
          <div className="relative flex justify-center gap-4 py-4">

            {/* Linha central */}
            <div className="absolute inset-x-0 top-[50%] h-12 border-y border-gray-300 pointer-events-none" />

            {/* HORAS */}
            <div
              ref={hourRef}
              onScroll={() => handleSnap(hourRef, hours, setSelectedHour)}
              className="w-20 h-48 overflow-y-scroll scroll-smooth snap-y snap-mandatory text-center"
            >
              {hours.map((h) => (
                <div
                  key={h}
                  className={`snap-start h-12 flex items-center justify-center text-xl font-bold transition
                  ${selectedHour === h ? "text-blue-600 scale-110" : "text-gray-500"}`}
                >
                  {h}
                </div>
              ))}
            </div>

            {/* MINUTOS */}
            <div
              ref={minuteRef}
              onScroll={() => handleSnap(minuteRef, minutes, setSelectedMinute)}
              className="w-20 h-48 overflow-y-scroll scroll-smooth snap-y snap-mandatory text-center"
            >
              {minutes.map((m) => (
                <div
                  key={m}
                  className={`snap-start h-12 flex items-center justify-center text-xl font-bold transition
                  ${selectedMinute === m ? "text-blue-600 scale-110" : "text-gray-500"}`}
                >
                  {m}
                </div>
              ))}
            </div>
          </div>

          {/* BOTÃO CONFIRMAR */}
          <Button
            className="w-full h-14 text-lg font-bold bg-blue-600 hover:bg-blue-700"
            onClick={() => onConfirm(`${selectedHour}:${selectedMinute}`)}
          >
            Confirmar
          </Button>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
}
