import { createPageUrl } from "../lib/utils";
import { createTask } from "../api/base44Client";
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { Textarea } from "../components/ui/textarea";
import { Label } from "../components/ui/label";
import { Card } from "../components/ui/card";

import { useState, useEffect } from "react";
import { base44 } from "../api/base44Client";
import { ArrowLeft, Save, Clock, Palette, Calendar } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { useQueryClient, useMutation } from '@tanstack/react-query';

const colorOptions = [
  { id: "red", defaultName: "Trabalho", bgClass: "bg-red-500 hover:bg-red-600", textClass: "text-white" },
  { id: "yellow", defaultName: "Estudos", bgClass: "bg-yellow-500 hover:bg-yellow-600", textClass: "text-white" },
  { id: "blue", defaultName: "Pessoal", bgClass: "bg-blue-500 hover:bg-blue-600", textClass: "text-white" },
  { id: "green", defaultName: "Saúde", bgClass: "bg-green-500 hover:bg-green-600", textClass: "text-white" }
];

const weekDayOptions = [
  { id: "everyday", shortName: "Todos", fullName: "Todos os dias" },
  { id: "monday", shortName: "Seg", fullName: "Segunda" },
  { id: "tuesday", shortName: "Ter", fullName: "Terça" },
  { id: "wednesday", shortName: "Qua", fullName: "Quarta" },
  { id: "thursday", shortName: "Qui", fullName: "Quinta" },
  { id: "friday", shortName: "Sex", fullName: "Sexta" },
  { id: "saturday", shortName: "Sáb", fullName: "Sábado" },
  { id: "sunday", shortName: "Dom", fullName: "Domingo" }
];

export default function CreateTask() {
  const navigate = useNavigate();
  const queryClient = useQueryClient();
  const [categoryNames, setCategoryNames] = useState({});
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    time: "",
    color: "blue",
    recurrence: [],
    active: true,
    alarm_enabled: true
  });

  useEffect(() => {
    loadCategoryNames();
  }, []);

  const loadCategoryNames = async () => {
    try {
      const categories = await base44.entities.Category.list();
      const names = {};
      categories.forEach(cat => {
        names[cat.color] = cat.name;
      });
      setCategoryNames(names);
    } catch (error) {
      console.error("Error loading categories:", error);
    }
  };

  const createTaskMutation = useMutation({
    mutationFn: (taskData) => base44.entities.Task.create(taskData),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['tasks'] });
      navigate(createPageUrl("Home"));
    },
  });

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const toggleWeekDay = (dayId) => {
    if (dayId === "everyday") {
      if (formData.recurrence.includes("everyday")) {
        setFormData(prev => ({ ...prev, recurrence: [] }));
      } else {
        setFormData(prev => ({ ...prev, recurrence: ["everyday"] }));
      }
    } else {
      if (formData.recurrence.includes("everyday")) {
        return;
      }
      
      setFormData(prev => ({
        ...prev,
        recurrence: prev.recurrence.includes(dayId)
          ? prev.recurrence.filter(d => d !== dayId)
          : [...prev.recurrence, dayId]
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    createTaskMutation.mutate(formData);
  };

  const getColorDisplayName = (colorId) => {
    const color = colorOptions.find(c => c.id === colorId);
    return categoryNames[colorId] || color?.defaultName || colorId;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 via-blue-50 to-purple-50">
      {/* Fixed Header */}
      <div className="sticky top-0 z-30 bg-white border-b border-gray-200 shadow-sm">
        <div className="max-w-2xl mx-auto px-4 py-4">
          <div className="flex items-center gap-4">
            <Button
              variant="ghost"
              size="icon"
              onClick={() => navigate(createPageUrl("Home"))}
              className="rounded-full"
            >
              <ArrowLeft className="w-6 h-6 text-gray-900" />
            </Button>
            <div>
              <h1 className="text-2xl font-black text-gray-900">Nova Tarefa</h1>
            </div>
          </div>
        </div>
      </div>

      {/* Scrollable Content */}
      <div className="max-w-2xl mx-auto px-4 py-6">
        <Card className="shadow-2xl border-0 bg-white p-6">
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Task Name */}
            <div className="space-y-3">
              <Label htmlFor="name" className="text-lg font-bold flex items-center gap-2 text-gray-900">
                <Clock className="w-5 h-5 text-blue-600" />
                Nome da Tarefa *
              </Label>
              <Input
                id="name"
                required
                placeholder="Ex: Reunião importante"
                value={formData.name}
                onChange={(e) => handleInputChange("name", e.target.value)}
                className="text-lg h-14 rounded-xl border-2 focus:border-blue-500 bg-white"
              />
            </div>

            {/* Description */}
            <div className="space-y-3">
              <Label htmlFor="description" className="text-lg font-bold text-gray-900">
                Descrição (opcional)
              </Label>
              <Textarea
                id="description"
                placeholder="Adicione mais detalhes sobre a tarefa..."
                value={formData.description}
                onChange={(e) => handleInputChange("description", e.target.value)}
                className="h-28 rounded-xl border-2 focus:border-blue-500 resize-none bg-white"
              />
            </div>

            {/* Time */}
            <div className="space-y-3">
              <Label htmlFor="time" className="text-lg font-bold flex items-center gap-2 text-gray-900">
                <Clock className="w-5 h-5 text-green-600" />
                Horário *
              </Label>
              <Input
                id="time"
                type="time"
                required
                value={formData.time}
                onChange={(e) => handleInputChange("time", e.target.value)}
                className="text-3xl h-16 rounded-xl border-2 focus:border-green-500 font-bold text-center bg-white"
              />
            </div>

            {/* Color Classification */}
            <div className="space-y-3">
              <Label className="text-lg font-bold flex items-center gap-2 text-gray-900">
                <Palette className="w-5 h-5 text-purple-600" />
                Classificação por Cor *
              </Label>
              <div className="grid grid-cols-4 gap-3">
                {colorOptions.map((color) => (
                  <motion.button
                    key={color.id}
                    type="button"
                    whileTap={{ scale: 0.9 }}
                    onClick={() => handleInputChange("color", color.id)}
                    className={`${color.bgClass} h-20 rounded-2xl transition-all duration-300 ${
                      formData.color === color.id
                        ? "ring-4 ring-offset-4 ring-gray-900 shadow-2xl scale-105"
                        : "shadow-lg hover:shadow-xl"
                    } ${color.textClass} font-bold text-sm flex items-center justify-center p-2`}
                  >
                    <span className="text-center break-words leading-tight">
                      {getColorDisplayName(color.id)}
                    </span>
                  </motion.button>
                ))}
              </div>
            </div>

            {/* Week Days Recurrence */}
            <div className="space-y-3">
              <Label className="text-lg font-bold flex items-center gap-2 text-gray-900">
                <Calendar className="w-5 h-5 text-orange-600" />
                Repetir nos Dias (opcional)
              </Label>
              <div className="grid grid-cols-4 gap-2">
                {weekDayOptions.map((day) => (
                  <motion.button
                    key={day.id}
                    type="button"
                    whileTap={{ scale: 0.95 }}
                    onClick={() => toggleWeekDay(day.id)}
                    disabled={day.id !== "everyday" && formData.recurrence.includes("everyday")}
                    className={`h-14 rounded-xl font-bold transition-all duration-300 ${
                      formData.recurrence.includes(day.id)
                        ? "bg-gradient-to-br from-blue-500 to-blue-700 text-white shadow-lg scale-105"
                        : "bg-white text-gray-600 hover:bg-gray-100"
                    } ${day.id !== "everyday" && formData.recurrence.includes("everyday") ? "opacity-50 cursor-not-allowed" : ""}`}
                  >
                    {day.shortName}
                  </motion.button>
                ))}
              </div>
              <p className="text-sm text-gray-500">
                {formData.recurrence.length === 0
                  ? "Tarefa acontecerá apenas no dia de criação"
                  : formData.recurrence.includes("everyday")
                  ? "Tarefa se repetirá todos os dias"
                  : `Irá repetir em ${formData.recurrence.length} dia(s) da semana`}
              </p>
            </div>

            {/* Submit Button */}
            <Button
              type="submit"
              disabled={createTaskMutation.isPending}
              className="w-full h-16 text-xl font-black bg-gradient-to-r from-green-500 via-green-600 to-green-700 hover:from-green-600 hover:via-green-700 hover:to-green-800 rounded-2xl shadow-2xl"
            >
              {createTaskMutation.isPending ? (
                <span className="flex items-center gap-2">
                  <Clock className="w-6 h-6 animate-spin" />
                  Criando...
                </span>
              ) : (
                <span className="flex items-center gap-2">
                  <Save className="w-6 h-6" />
                  Criar Tarefa
                </span>
              )}
            </Button>
          </form>
        </Card>
      </div>
    </div>
  );
}
