const API_BASE = "http://localhost:3001";

// ---------------- TASKS ----------------
async function listTasks() {
  const res = await fetch(`${API_BASE}/tasks`);
  return res.json();
}

async function createTask(data) {
  const res = await fetch(`${API_BASE}/tasks`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  return res.json();
}

async function updateTask(id, data) {
  const res = await fetch(`${API_BASE}/tasks/${id}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  return res.json();
}

async function deleteTask(id) {
  const res = await fetch(`${API_BASE}/tasks/${id}`, {
    method: "DELETE",
  });
  return res.json();
}

// ---------------- CATEGORIES ----------------
async function listCategories() {
  const res = await fetch(`${API_BASE}/categories`);
  return res.json();
}

async function createCategory(data) {
  const res = await fetch(`${API_BASE}/categories`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  return res.json();
}

async function updateCategory(id, data) {
  const res = await fetch(`${API_BASE}/categories/${id}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  return res.json();
}

// ---------------- COMPLETIONS ----------------
async function filterCompletions(query) {
  const params = new URLSearchParams(query).toString();
  const res = await fetch(`${API_BASE}/task-completions?${params}`);
  return res.json();
}

async function createCompletion(data) {
  const res = await fetch(`${API_BASE}/task-completions`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  return res.json();
}

async function updateCompletion(id, data) {
  const res = await fetch(`${API_BASE}/task-completions/${id}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  return res.json();
}

// ---------------- API OBJECT ----------------
const api = {
  Task: {
    list: listTasks,
    create: createTask,
    update: updateTask,
    delete: deleteTask,
  },

  Category: {
    list: listCategories,
    create: createCategory,
    update: updateCategory,
  },

  TaskCompletion: {
    list: filterCompletions,      // lista por query
    filter: filterCompletions,    // alias
    create: createCompletion,
    update: updateCompletion,
  }
};

export default api;
